mapper.select:
SELECT proxy_id, fee_record_id, department_id, original_user_id, original_real_name, original_student_number,
       proxy_user_id, proxy_real_name, proxy_student_number, article_title, fee_amount, proxy_month, created_at, updated_at
FROM proxy_receipt_records WHERE proxy_id = #{proxyId}

SELECT proxy_id, fee_record_id, department_id, original_user_id, original_real_name, original_student_number,
       proxy_user_id, proxy_real_name, proxy_student_number, article_title, fee_amount, proxy_month, created_at, updated_at
FROM proxy_receipt_records ORDER BY proxy_id LIMIT #{offset}, #{limit}
